    el = util.etree.Element("img")
    src_parts = m.group(9).split()
    if src_parts:
        src = src_parts[0]
        if src[0] == "&lt;" and src[-1] == "&gt;":
            src = src[1:-1]
        el.set('src', self.sanitize_url(self.unescape(src)))
    else:
        el.set('src', "")
    if len(src_parts) &gt; 1:
