Source code in <code>pyrevitlib/pyrevit/coreutils/markdown/extensions/toc.py</code> <pre><code>def run(self, doc):
    # Get a list of id attributes
    used_ids = set()
    for el in doc.iter():
        if "id" in el.attrib:
            used_ids.add(el.attrib["id"])

    toc_tokens = []
    for el in doc.iter():
        if isinstance(el.tag, string_type) and self.header_rgx.match(el.tag):
            self.set_level(el)
            text = ''.join(el.itertext()).strip()

            # Do not override pre-existing ids
            if "id" not in el.attrib:
                innertext = stashedHTML2text(text, self.markdown)
                el.attrib["id"] = unique(self.slugify(innertext, self.sep), used_ids)

            toc_tokens.append({
                'level': int(el.tag[-1]),
                'id': el.attrib["id"],
                'name': text
            })

            if self.use_anchors:
                self.add_anchor(el, el.attrib["id"])
            if self.use_permalinks:
                self.add_permalink(el, el.attrib["id"])

    div = self.build_toc_div(nest_toc_tokens(toc_tokens))
    if self.marker:
        self.replace_marker(doc, div)

    # serialize and attach to markdown instance.
    toc = self.markdown.serializer(div)
    for pp in self.markdown.postprocessors.values():
        toc = pp.run(toc)
    self.markdown.toc = toc
</code></pre>

